/* Quantos Ã­cones aparecem */
const SLOT_COUNT = 1;

/* Intervalo entre trocas */
const SWAP_INTERVAL = 2000;

/* Utils */
function getRandomIndex(exclude: number[], max: number): number {
  let idx: number;
  do {
    idx = Math.floor(Math.random() * max);
  } while (exclude.includes(idx));
  return idx;
}

function initSlots(): number[] {
  const indices: number[] = [];
  for (let i = 0; i < SLOT_COUNT; i++) {
    indices.push(i % techIcons.length);
  }
  return indices;
}

export function TechIconColumn() {
  const [slots, setSlots] = useState<number[]>(initSlots);
  const [animatingSlot, setAnimatingSlot] = useState<number | null>(null);
  const [phase, setPhase] = useState<"out" | "in">("in");

  const swapSlot = useCallback(() => {
    const slotIdx = Math.floor(Math.random() * SLOT_COUNT);
    const currentIcons = slots;
    const newIconIdx = getRandomIndex(currentIcons, techIcons.length);

    /* Fade out */
    setAnimatingSlot(slotIdx);
    setPhase("out");

    /* Troca + fade in */
    setTimeout(() => {
      setSlots((prev) => {
        const next = [...prev];
        next[slotIdx] = newIconIdx;
        return next;
      });
      setPhase("in");

      setTimeout(() => {
        setAnimatingSlot(null);
      }, 500);
    }, 500);
  }, [slots]);

  useEffect(() => {
    const interval = setInterval(swapSlot, SWAP_INTERVAL);
    return () => clearInterval(interval);
  }, [swapSlot]);

  return (
    <div className="hidden lg:flex flex-col items-center justify-center gap-6 w-64">
      {slots.map((iconIdx, slotIdx) => {
        const icon = techIcons[iconIdx];
        const isAnimating = animatingSlot === slotIdx;
        const isOut = isAnimating && phase === "out";

        return (
          <div
            key={slotIdx}
            className="h-32 w-32 flex items-center justify-center"
          >
            <div
              className="h-28 w-28 transition-all duration-500 ease-in-out"
              style={{
                opacity: isOut ? 0 : 1,
                transform: isOut ? "scale(0.6)" : "scale(1)",
              }}
              dangerouslySetInnerHTML={{ __html: icon.svg }}
            />
          </div>
        );
      })}
    </div>
  );
}
